void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float angle = iTime;
    vec2 origin = vec2(0.5, 0.5);
    uv -= origin.xy;
    origin.y = 20.0*cos(iTime)*origin.y;
    vec2 rot = vec2(uv.x * cos(angle) - uv.y * sin(angle),
                    uv.x * sin(angle) + uv.y * cos(angle));
    rot += origin.xy;

    vec3 col = vec3(cos(rot.y+cos(20.0*rot.x+iTime)+iTime),
                    cos(rot.y+cos(20.0*rot.x+iTime)),
                    cos(rot.y+cos(20.0*rot.x+iTime)-iTime));


    // Output to screen
    fragColor = vec4(sqrt(col),0.5);
}
