// Plento
const int nBars = 35;
float speed = 60.; 
const float unq = 600.;

void mainImage( out vec4 f, in vec2 u ){

    vec2 uv = u / iResolution.xy;

    vec4 b = texture(iChannel0, uv);

    vec3 col = vec3(step(b.w/unq, -1.*(uv.y-1.)));
    float sch = mod(floor(iTime*speed), float(nBars));

    float id = floor(uv.x * float(nBars));
    if(id == sch)col *= vec3(1.0, 0., 0.);

    f = vec4(col, 1.0);
}

const int nBars = 35;
float speed = 60.; 
const float unq = 600.;
vec2 R;
float read(float offset){
 	return texture(iChannel0, vec2(offset + 0.001, 0.)).w;
}

float rnd(float p){
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}
void mainImage( out vec4 f, in vec2 u ){
    R = iResolution.xy;
    vec2 uv = u / R;

    vec4 b = texture(iChannel0, uv);

    float cID = mod(floor(iTime*speed), float(nBars));
    float ratio = 1. / float(nBars);

    float rs0 = cID * ratio ; 
    float rs1 = (cID + 1.) * ratio ; 

    float i0 = read(rs0);
    float i1 = read(rs1);

	if(i0 < i1){
		if(uv.x > rs0 && uv.x < rs0 + ratio)
			b.w = i1;
        else if(uv.x > rs1 && uv.x < rs1 + ratio)
        	b.w = i0;
    } 

    b = vec4(i0, i1, b.z, b.w);

    float id = floor(uv.x * float(nBars));
    if(iFrame < 2)b = vec4(floor(rnd(id + 23.34)*unq));

    f = b;    
}
