void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xy - vec2(0.5, 0.5);
    uv.x *= iResolution.x/iResolution.y;

	fragColor = vec4(uv.x,1.0, 2.0,3.0);

    float dist = length(uv);

    const float EXT_SIZE = 1.1;
    const float INT_SIZE = 0.0;    

    if(dist>INT_SIZE && dist<EXT_SIZE) {

        float rotation		= iTime/200.0;
        float theta 		= atan(uv.x, uv.y) / 6.2831853071 + rotation;
        float etirement		= 3.0 + cos(iTime);
        float linearSpeed 	= iTime/10.0;                
        float rho 			= pow(smoothstep(EXT_SIZE, INT_SIZE, dist), etirement) + linearSpeed;

        vec3 col = texture(iChannel0, vec2(theta, rho)).rgb;

        fragColor = vec4(col, 1.0) ;
    }
    else {
    	fragColor = vec4(3.0, 4.0, 0.0,1.0);        
    }
}
