void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 src = texture(iChannel0, uv);
    vec4 dst = texture(iChannel1, uv);
    vec4 rule = texture(iChannel2, uv);
    vec4 maskcolor = vec4(1.0);

    float progress = mod(iTime,10.0)/10.0;
    float vague = 0.;

    float gray = dot(rule.rgb,vec3(0.3,0.59,0.11));
    vec4 final;

    if (progress <= 0.5) {
        float m = (1.0 + vague)*progress * 2.0;
        float per = 1.0 - smoothstep(m - vague, m, gray);
        final = mix(src,maskcolor,per);
    }
    else {
        float m = (1.0 + vague)*(progress - 0.5) * 2.0;
        float per = 1.0 - smoothstep(m - vague, m, gray);
        final = mix(maskcolor,dst,per);
    }

    // Output to screen
    fragColor = final;
}
