void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = vec4(col,1.0);

    vec2 orig1 = vec2(0.2, 0.0);
    vec2 orig2 = vec2(0.8, 0.0);
    float frec1 = 100.0;
    float frec2 = 100.0;
    float timeRed = 0.2 * iTime;

    float dist1 = length(uv - orig1);
    float dist2 = length(uv - orig2);

    float wave1 = sin(frec1 * (timeRed - dist1));
    float wave2 = sin(frec2 * (timeRed - dist2));
	wave1 = (wave1+1.0)*0.5;
    wave2 = (wave2+1.0)*0.5;


    float decay1 = clamp(0.2/dist1, 0.0, 1.0);
    float decay2 = clamp(0.2/dist2, 0.0, 1.0);

    float timeDecay1 = 1.0;
    float timeDecay2 = 1.0;

    if ( dist1 > timeRed ) timeDecay1=0.0; 
    if ( dist2 > timeRed ) timeDecay2=0.0; 

    wave1 *= timeDecay1;
    wave2 *= timeDecay2;

    //wave1 *= decay1;
    //wave2 *= decay2;

    //fragColor *= wave1;
    fragColor *= (wave1 + wave2) * 0.5;
}
