// based on...
// https://www.shadertoy.com/view/lssfW2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 curve_p = vec2(0,0);
    float d = 10000.0;
    float di = 0.;
    for(int i = 0;i<2000;i++)
    {
        di = float(i)*.03;
        curve_p.x = -.1*sin(.3*iTime)+.05*float(di)*sin(float(di)+.2*iTime) +.01*float(di)*sin(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*sin(7.0*di+.4*iTime);
        curve_p.y = -.1*cos(.3*iTime)+.05*float(di)*cos(float(di)+.2*iTime) +.01*float(di)*cos(4.0*di+.2*iTime) + .03*sin(iTime*.1)*float(di)*cos(7.0*di+.4*iTime);
        d = min(d, length(p-curve_p));

    }
    float r =5.*d*d + (.025 - .025*pow(dot(p, curve_p),2.0));
    float g = .008 + .5*sin(.001*d*d+.1*length(p))+.03*dot(p,curve_p + .0005*vec2(iTime, iTime));
    float b=.025*abs(.15+.13*cos(iTime*.8 - 2.5*pow(d,8.0))) +.01*(dot(p,curve_p - .005*vec2(iTime-200.05, iTime-200.05)+10.0));
    vec3 col = vec3(r, g, b);
    float dn = col.x + col.y + col.z;
    col /= dn;
    // Output to screen
    fragColor = vec4(col,1.0);
}
