void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x /= iResolution.y / iResolution.x;

    uv.xy /= clamp(abs(uv.x)*0.5 - 0.05, 0., 1.) - 0.13;
    uv.xy /= clamp(abs(uv.y)*0.5 - 0.5, 0., 1.) - 0.13;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 cb = mod(vec3(uv.x * cos(iTime * 0.1) - uv.y * sin(iTime * 0.1), 
                       uv.x * sin(iTime * 0.1) + uv.y * cos(iTime) * 0.1, 0.) + vec3(iTime, 0., 0.), 5.0) * 0.3;
    cb.z = 0.;

    col = cb / exp(length(uv * 0.07));

    fragColor = vec4(col,1.0);
}
