void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //set page size
    vec2 uv = fragCoord/iResolution.xy*2.;

    //set speed
    float nTime=iTime*.3;

    //rotate page
    float s = sin(nTime*.1);
    float c = cos(nTime*.1);
    uv*=mat2(c, -s, s, c); 

    //colour formulas
    float nFact=10.+50.*(sin(nTime*.3)+1.);
    float col1=radians((uv.x*uv.y)*nFact);
    float col2=cos((uv.x+sin(uv.y))*nFact);
    float col3=cos((uv.x*uv.y)*nFact);

    //apply colour
    fragColor = mix(vec4(col1+col2+col3,col1,col1+col2+col3,1.0),vec4(col1,col1+col2+col3,col3,1.0),sin(nTime+uv.x));
}
