vec3 grid(vec3 grid_col, vec3 main_col, vec2 uv) {
    float vert_line_0 = step(0.498, uv.x);
    float vert_line_1 = step(0.502, uv.x);

    vec3 vert_result = mix(main_col, grid_col, vert_line_0);
    vert_result = mix(vert_result, main_col, vert_line_1);

    float hor_line_0 = step(0.495, uv.y);
    float hor_line_1 = step(0.505, uv.y);  

    vec3 hor_result = mix(main_col, grid_col, hor_line_0);
    hor_result = mix(hor_result, main_col, hor_line_1);

    return vert_result * hor_result;
}

vec3 frame_0(vec3 col0, vec3 main_col, vec2 uv) {
    float y0 = step(0.1, uv.x);
    float y1 = step(0.12, uv.x);
    float y2 = step(0.6, uv.y);
    float y3 = step(0.9, uv.y);

    vec3 vert_result = mix(main_col, col0, y0);
    vert_result = mix(vert_result, main_col, y1);

    vec3 hor_result = mix(main_col, col0, y2);
    hor_result = mix(hor_result, main_col, y3);

	return vert_result + hor_result;
}

vec3 frame_1(vec3 col0, vec3 col1, vec3 main_col, vec2 uv) {
    float y0 = step(0.6, uv.x);
    float y1 = step(0.62, uv.x);
    float y2 = step(0.6, uv.y);
    float y3 = step(0.9, uv.y);
    float y4 = step(0.8, uv.x);
    float y5 = step(0.82, uv.x);

    vec3 result = mix(main_col, col0, y0);
    result = mix(result, main_col, y1);
    result = mix(result, col1, y4);
    result = mix(result, main_col, y5);

    vec3 result_0 = mix(main_col, col0, y2);
    result_0 = mix(result_0, main_col, y3);

	return result + result_0;
}

vec3 frame_2(vec3 col0, vec3 col1, vec3 main_col, vec2 uv) {
    float y0 = step(0.1, uv.x);
    float y1 = step(0.12, uv.x);
    float y2 = step(0.1, uv.y);
    float y3 = step(0.4, uv.y);
    float y4 = step(0.3, uv.x);
    float y5 = step(0.32, uv.x);

    vec3 result = mix(main_col, col0, y0);
    result = mix(result, main_col, y1);
    result = mix(result, col1, y4);
    result = mix(result, main_col, y5);

    vec3 result_0 = mix(main_col, col0, y2);
    result_0 = mix(result_0, main_col, y3);

	return result + result_0;
}

vec3 frame_3(vec3 col0, vec3 col1, vec3 main_col, vec2 uv) {
    float y0 = step(0.6, uv.x);
    float y1 = step(0.62, uv.x);
    float y2 = step(0.1, uv.y);
    float y3 = step(0.4, uv.y);

    vec3 result = mix(main_col, col0, y0);
    result = mix(result, main_col, y1);

    vec3 result_0 = mix(main_col, col0, y2);
    result_0 = mix(result_0, main_col, y3);

    float y4 = step(0.7, uv.x);
    float y5 = step(0.9, uv.x);
    float y6 = step(0.1, uv.y);
    float y7 = step(0.14, uv.y);

    vec3 result_1 = mix(main_col, col1, y4);
    result_1 = mix(result_1, main_col, y5);

    vec3 result_2 = mix(main_col, col1, y6);
    result_2 = mix(result_2, main_col, y7);

	return (result + result_0) * (result_1 + result_2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 blue = vec3(0.0, 0.0, 1.0);

    vec3 _grid = grid(vec3(0.0), vec3(1.0), uv);
    vec3 _frame_0 = frame_0(blue, vec3(1.0), uv);
    vec3 _frame_1 = frame_1(blue, blue, vec3(1.0), uv);
    vec3 _frame_2 = frame_2(blue, blue, vec3(1.0), uv);
    vec3 _frame_3 = frame_3(blue, blue, vec3(1.0), uv);
    vec3 col = _grid * _frame_0 * _frame_1 * _frame_2 * _frame_3;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
