#define PI 3.14159265359
#define TWO_PI 6.28318530718
#define red vec4(1.0, 0.0, 0.0, 0.0)
#define green vec4(0.0, 1.0, 0.0, 0.0)
#define blue vec4(0.0, 0.0, 1.0, 0.0)
#define white vec4(1.0, 1.0, 1.0, 1.0)
#define grey vec4(0.5, 0.5, 0.5, 0.25)

//Rectangle Function
float square(vec2 uv, vec2 pos, float width, float height){
    uv = uv+pos;
    vec2 bl = step(abs(uv*2.0-1.0), vec2(width));  //Width
    vec2 b2 = step(abs(uv*2.0-1.0), vec2(height)); //Height
    float shape = bl.x * b2.y;

	return shape;
}

//Grid Function
float grid(vec2 uv, float res){
    vec2 grid = fract(uv*res);
    return 1.0-(step(res, grid.x)*step(res, grid.y));
}

//Rotate Function
mat2 rotateObj(float angle){
    return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

//Scale Function
mat2 scaleObj(vec2 scale){
    return mat2(scale.x, 0.0, 0.0, scale.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
   	vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(0.0);

    //Make the grid
    vec2 grid_uv = uv*300.;
    color += vec3(1.0, 1.0, 1.0)*grid(grid_uv,0.01);
    color += vec3(0.5, 0.5, 0.5)*grid(grid_uv,0.02);
    color += vec3(0.2, 0.2, 0.2)*grid(grid_uv,0.1);

    //Rotation point at (3, 3)
    uv-=vec2(0.1);
    //Rotate -45 degree
    uv = rotateObj(sin(-45.0))*uv;
    //Put back to original place
    uv+=vec2(0.1);
    //Scale to 1/3 of origin size
    uv = scaleObj(vec2(1.0*3.0))*uv;
    //Make the square
    float Square = square(uv, vec2(0.45, 0.45), 0.1, 0.1);
    color += Square;

    fragColor = vec4(color, 1.0);
}
