void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv   = fragCoord/iResolution.xy;

    float vt  = texture(iChannel1, vec2(0.5) + vec2(sin(iTime * 0.2) * 0.1, 0.0)).x;

    float f   = texture(iChannel1, (uv - 0.5) * (vt * 0.2 + 0.8) + 0.5).x;  

    vec3  col = texture(iChannel0, (uv - 0.5) * f + 0.5).xyz;

    // vignette
    float v   = length(uv * 2.0 - 1.0);
    v *= v * (1.0 - vt);
    col *= (1.0 - 0.9 * v);

    fragColor = vec4(col,1.0);
}
