vec3 viiva(vec2 uv,float t)
{

    float n;
    float h = 4.;
    float f = 5.4;
    float c = step(mod(uv.x*40.,2.),.5);
 	n = abs(step(uv.y*h,sin(uv.x*f+t))- step(uv.y*h,cos(uv.x*f+t)));
    vec3 y = vec3(n*c*abs(sin(uv.x*49.+t)),n*c*abs(sin(uv.x*149.+t)),n*c*abs(sin(uv.x*89.+t)));
    return y;
}
vec3 ympyra(vec2 offset,vec2 uv,float r,float v ,float t)
{
    float c =  step(0.,1.- length(uv+offset)-r)-step(0.,1.- length(uv+offset)-(r+0.01));
    vec3 y= vec3(abs(sin(v*190.)*c),abs(sin(v*200.)*c),abs(sin(v*100.)*c));
    return y;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2. - 1.;  
    uv.x *= iResolution.x / iResolution.y;
	float skip = 2.;
    float ymp = 2.;
     float t = iTime/2.;
    float vari;
    vec3 c;
     c += viiva(uv,t);
        for(float i = 0.; i < 3.1415*ymp; i= i + 0.1){
            	 c += ympyra(vec2(sin(i*skip),cos(i*skip+t))*0.4,uv,.97,i,t);
        }
      for(float i = 0.; i < 3.14*ymp; i= i + 0.1){
            	 c += ympyra(vec2(sin(i*skip+t),cos(i*skip))*0.65,uv,.96,i,t);
        }
      for(float i = 0.; i < 3.14*ymp; i= i + 0.1){
            	 c += ympyra(vec2(sin(i*skip+t),cos(i*skip+t))*0.9,uv,.95,i,t);
        }


    fragColor = vec4(c,1.0);
}
