void mainImage( out vec4 fragColor, in vec2 fragCoord )//A function to accept both out(vec4 fragColor) and in-type(vec2 fragCoord).
{
    float mouseX = iMouse.x; 
    //declaration of float type variable that get the x coordinates of the mouse
    float mouseY = iMouse.y;
    //declaration of float type variable that get the y coordinates of the mouse

    fragColor = vec4(sin(mouseX), cos(mouseY) ,abs(sin(iTime)),0);
    //Colors change depending on the coordinates of the mouse and automatically.
}
