bool inrange(float val, float from, float to)
{
	return from < val && val < to;
}

float f(float x, float y)
{
	return cos(x/(30.0)+iTime+(iMouse.x/200.0))*
        (sqrt(x)*sin(iTime))+
        ((cos(iTime)+iTime*10.0+(iMouse.y/100.0))/(iResolution.x/1000.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float m = mod(
        fragCoord.y+f(fragCoord.x, 
                      fragCoord.y), 
        20.0+((cos(fragCoord.x/20.0)+1.0)/2.0)/40.0
    );
    if (inrange(
        m, 
        0.0, 
        10.0*clamp((sin(fragCoord.x*smoothstep(iTime/fragCoord.x, 0.0, 1.0))+1.0)/6.0, 0.5, 0.9)
    ))
    {
    	fragColor = vec4(0, 0, 0, 1.0);
    }
    else
    {
    	fragColor = vec4(0.98, 0.0, 0.2, 1.0);
    }
}
