void mainImage(out vec4 O, vec2 C ) {
    for (float T, D, i = 0.; i < 99.; i += .1) {
        vec3 B, P = vec3((C.xy - iResolution.xy * .5) / iResolution.y, 2) * T;
        P.y += sin(P.x + iTime) * .3 - 9.;
        P.xy *= mat2(cos(T + vec4(0, 5, 8, 0)));
        B = abs(mod(P + iTime, 10.) - 5.) - vec3(2, .01, .3);
        T += D = (length(max(B, 0.)) + min(max(B.x, max(B.y, B.z)), 0.)) * .02;
        O += (D < .001) ? .05 / i : 0.;
    }
}
