const float TWO_PI = 6.283185307;
const float S = 10.0;

vec2 rnd2(vec2 s) { return fract(sin(vec2(dot(s,vec2(127.1,311.7)),dot(s,vec2(269.5,183.3))))*43758.5453); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float mr = min(iResolution.x, iResolution.y);
    vec2 uv = (fragCoord*2.0-iResolution.xy)*S/mr;
    vec2 iuv = floor(uv);
    vec2 fuv = fract(uv);

    float t = fract(iTime)*TWO_PI;
    float md = 10.0;
   	vec2 cuv = iuv;
    for(int x = -1; x <= 1; ++x){
        for(int y = -1; y <= 1; ++y){
            vec2 n = vec2(x, y);
            vec2 rp = 0.5+sin(t+rnd2(iuv+n)*TWO_PI)*0.5;
            float l = length(n+rp-fuv);
            if (md > l) cuv = iuv+n+rp;
            md = min(md, l); 
        }
    }

    vec2 tuv = mr*0.5/S*cuv/iResolution.xy+0.5;
    fragColor = texture(iChannel0, tuv);
}
