void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float hArray[4];
    hArray[0] = 0.25;
    hArray[1] = 0.35;
    hArray[2] = 0.55;
    hArray[3] = 0.85;
    vec3 sum = vec3(0.0);
    for(int i =0;i<5; i++){
    float dotwidth =(sin(iTime) + 1.0)/2.0;
    float targetHeight = hArray[i];
    float f = smoothstep(dotwidth-0.035,dotwidth,pow(uv.x,1.0+sin(iTime)*targetHeight/4.0))-smoothstep(dotwidth,dotwidth+0.035,pow(uv.x,1.0+sin(iTime)*targetHeight/4.0)) + 0.5;
    float height = uv.y;
        //+fract(sin(iTime));
    float width = 0.05;
    float line = f*(smoothstep(targetHeight-width, targetHeight, height) - smoothstep(targetHeight, targetHeight+width, height));
	float s = (sin(100.0*targetHeight)+1.0)/2.0;
    vec3 color = vec3(line*(1.0-s),(s)*line,(1.0+s)*line);
    sum += color;
    }
    // Output to screen
    fragColor = vec4(sum,1.0);
}
