float drawMetaballAt(vec2 pos, float radius, vec2 uv)
{
    float m = 1.-length(pos - uv) / radius;
    if (m < 0.0) return 0.;
    return pow(m, 3.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float final = drawMetaballAt(vec2(0), 1.0, uv);


    if (iMouse.z > 0.)
    	final += drawMetaballAt((iMouse.xy-.5*iResolution.xy)/iResolution.y, 0.2, uv);

  	final = smoothstep(0.6, 0.603, final);

    vec3 col = final * vec3(1);

    fragColor = vec4(col,1.0);
}
