const float pi = radians(180.);


bool approx(float a, float b, float d)
{
    return abs(a-b)<=d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 mh = (iMouse.xy*2.-iResolution.xy)/iResolution.y;

    uv *= 1.;

    bool turn = mod(iTime, 16.) < 8.;

    float power = 2./(cos(iTime*pi/4.)+1.)-1.;
    float lhs =
        pow(abs(uv.x-uv.y*float(turn)), power) +
        pow(abs(uv.y+uv.x*float(turn)), power)
    ;
    float rhs = 1.;

    //if (approx(lhs, rhs, 0.1))
    fragColor = vec4(lhs <= rhs);
}
