// textured variant of https://shadertoy.com/view/3sXSW8

#define C(x,y) sin(1e3*length (ceil(U)+vec2(x,y))) > .3//
//
void mainImage(out vec4 O, vec2 u) {
    vec2 R = iResolution.xy, w,v,
         U =  ( u+u - R ) / R.y * mat2(1,-2,1,2);
    U.y += iTime;
    R = fract( U *= 5. );
    bool r = R.x+R.y > 1.;
    w = .5+cos(12.6*R*mat2(1,1,0,1));
    vec4 a = max(w.xxxx,w.y),
         b = texture(iChannel1,U/8.).grba,
         c = .4+.1*cos(12.6*(R.y+R.xxxx));
    O +=  C(1,-1)       ? 1.7*texture(iChannel0,U/4.)
        : C(1, 0) &&  r ? a
        : C(0,-1) && !r ? c
        : C(0, 0) ?   r ? c : a
        : C(-1,0)       ? b*.5
        :                 b;
}
