#define PI=3.14

vec3 hsv(float h,float s,float v)
{
	vec4 t=vec4(1.0,2.0/3.0,1.0/3.0,3.0);
    vec3 p=abs(fract(vec3(h)+t.xyz)*6.0-vec3(t.w));
    return v*mix(vec3(t.x),clamp(p-vec3(t.x),0.0,1.0),s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p=(fragCoord.xy*2.0-iResolution.xy)/iResolution.y;
    vec3 line=vec3(0.);

    for(float fi=0.0;fi<50.0;++fi)
    {
    	float offset=fi*3.14/20.0;
        float value=1.0+sin(iTime*fi*0.15+0.1)*0.5;
        float timer=iTime+fi*0.01;
        vec3 color=hsv((fi+iTime)*0.0675,1.0,value);
        line+=0.0015/abs(p.y+sin(p.x*1.0+offset+timer)*1.0+0.93)*color;
    }

    fragColor = vec4(line,1.0);
}
