float getMatte(vec4 col, vec3 key, float minDist, float maxDist) {
    vec3 delta = col.rgb - key;
    float d2 = dot(delta, delta);
    return clamp((d2 - minDist) / (maxDist - minDist), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
	vec4 col = texture(iChannel0, uv);

    float matte = getMatte(col, vec3(0.1, 0.9, 0.0), 0.23, 0.37);

    if (uv.x < 0.5) fragColor = vec4(vec3(matte), 1.0);
    else fragColor = mix(texture(iChannel1, uv), col, matte);
}
