void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = 0.01;
    //Get camera feed
    vec4 color = texture(iChannel0,fragCoord/iResolution.xy);
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(0.0 - width, 0));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(0.0 - width, width));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(0, width));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(width, width));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(width, 0));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(width, 0.0 - width));
	color += texture(iChannel0,fragCoord/iResolution.xy + vec2(0, 0.0 - width));
    color += texture(iChannel0,fragCoord/iResolution.xy + vec2(0.0 - width, 0.0 - width));

    // Output to screen
    fragColor = color / 9.0;
}
