void mainImage( out vec4 o, vec2 I )
{
    I /= iResolution.xy;
    o -= o; 
    I.x-=.5*I.y; 
    for(int i = 0; i < 9; i++)
        I = 2.*fract(I),
        I.x>=1.&&I.y>=1. ? o++ : o;
}
