/// by Dom Mandy in 2020


/*
mandelbrot generating function
*/
vec2 quad(vec2 z, vec2 c) {
    vec2 zNew;
    zNew.x = z.x * z.x - z.y * z.y;
    zNew.y = 2. * z.x * z.y;

    zNew += c;

    return zNew;
}


void mainImage(out vec4 fragColor, vec2 fragCoord) {
    // low res
    const float derez = .5;
    vec2 uv = fragCoord / iResolution.xy;
    vec2 r = derez * iResolution.xy;
    fragCoord = .5 + floor(derez * fragCoord);
    // composition
    const float scale = 4.125;
    // mouse controls as requested by FabriceNeyret2
    vec2 zoom = step(iMouse.zw, vec2(0.01)) + step(0.01, iMouse.xy) * mix(1., .001, smoothstep(0., .25 * length(iResolution.xy), length(iMouse.zw - iMouse.xy)));
    uv -= vec2(.5);
    uv *= zoom;
    // scale appropriatly
    uv *= scale;
    uv.x *= r.x / r.y;
    // 
    uv += step(0.01, iMouse.xy) * scale * vec2(r.x / r.y, 1.) * mix(iMouse.zw / iResolution.xy - vec2(.5), vec2(0.), clamp(zoom, 0., 1.));
    // mandelbrot
    vec2 z = vec2(0.);
    vec2 c = uv;
    bool isValid = false;
    float exit = 0.;
    float count= 17.;
    count += round(mix(40., 0., zoom.x));
    for(int i = 0; float(i) < count; i++)
    {
        isValid = isValid || dot(z, z) > 4.;
        exit = float(isValid) * exit + float(!isValid) * float(i);
        z = quad(z, c);
    }
    exit /= float(count-1.);
    // 4x4 bayer matrix yielding 17 tones to match the 17 potential exit times
    float threshold = (texture(iChannel0, mod(fragCoord, 4.) / 8.).r * 63. - 3.) / (4. * 15.);
	vec3 color = vec3(float(threshold > exit));

    fragColor = vec4(color, 1.);
}


