const float pi = 3.141592653589;
float mmod(float a, float b) { return a - floor(a/b); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 c1 = vec2(0.5,0.5);
    float n = 0.0 + 1.0*sin(2.0*pi*mmod(iTime,1.0));
    float a = 3.0*mod(length(uv.x-0.5),0.3);
    float b = 3.0*mod(length(uv.y-0.5),0.3);
    vec3 col = vec3(0,1,0) * ( cos(pi * 15.0 * a + cos(2.0 * pi * n*n)) * sin(pi * 15.0 * b + sin(2.0 * pi * n*n)) );

    fragColor = vec4(col,1.0);
}
