float circ(vec2 p){
    return length(p) - .5;   
}

// https://iquilezles.org/articles/palettes
// As t runs from 0 to 1 (our normalized palette index or domain), 
//the cosine oscilates c times with a phase of d. 
//The result is scaled and biased by a and b to meet the desired constrast and brightness.
vec3 cosPalette( float t, vec3 a, vec3 b, vec3 c, vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 sinPalette( float t, vec3 a, vec3 b, vec3 c, vec3 d )
{
    return a + b*sin( 6.28318*(c*t+d) );
}

vec2 lissajous(float t, float a, float b, float del){
    return vec2(sin(a*t+del),sin(b*t));
}

// Repeat around the origin by a fixed angle.
// For easier use, num of repetitions is use to specify the angle.
float pModPolar(inout vec2 p, float repetitions) {
    float angle = 6.2831/repetitions;
    float a = atan(p.y, p.x) + angle/2.;
    float r = length(p);
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.;
    p = vec2(cos(a), sin(a))*r;
    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 pos = fragCoord/iResolution.xy-vec2(.5,.5);

    pos.x = pos.x * iResolution.x/iResolution.y;

    pModPolar(pos, 1000.);

    float changeMe1 = 1000.0;
    float changeMe2 = 100.0;

    vec2 lisa = lissajous(pos.x*changeMe1, changeMe2*sin(iTime*.01),iTime/100.0, 3.14);

    pos += lisa;


    vec3 cosp = cosPalette(iTime*.5,vec3(0.5),vec3(0.5),vec3(.3),vec3(pos.x*.2,pos.y*.2,pos.x*pos.y*.2));

    vec3 col = vec3(circ(pos))*cosp;

    fragColor = min(vec4( col, 1.0 ), vec4(.8));
}
