void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // - Normalize Coordinate -
    vec2 uv = -(2. * fragCoord - iResolution.xy) / iResolution.y;

    // - Deform Space - 
    uv.y *= 3. + cos(uv.x * 5.);

    float offset = 0.25 + (sin(iTime * 2.) + 1.) * 0.5;
    float size = 2.;

    vec3 col = vec3(sin((offset - uv.y) * size), cos(uv.y * size), sin((uv.y + offset) * size));

    // - Output -
    fragColor = vec4(col, 1.0);
}
