float circle(vec2 uv,float distanceAa, float distanceAb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb));

	val = step(val, size);

    return val;
}


float gear(vec2 uv,float posX,float posY,float handSize,float midSize,float handNum)
{
    float ratio = iResolution.x / iResolution.y;

    uv.x *= ratio;

    vec2 shapePos = vec2(posX * ratio, posY);

    float dist = distance(shapePos, uv) * 2.0;

    uv -= shapePos;

    float speed = 0.5;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = 0.0;


    radius = smoothstep(-0.5, 1.0, cos(angle * handNum)) * handSize + midSize;


    float value = 1.0 - step(radius, dist);

    return value;

}

float quad(vec2 uv,float w, float h,float posX, float posY)
{
    float quad = step(posX-h ,uv.x)-step(posX+h,uv.x);
	quad = quad + step(posY-w,uv.y)-step(0.,uv.y)-step(posY+w,uv.y);
    return quad;
}
