    vec3 blue= vec3(.0,.0,1.0);
    vec3 black = vec3(0.,0.,0.);
    vec3 green = vec3(0,1.,0.);
    vec3 gray = vec3(.5,.5,.3);
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;
	vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	vec3 val = mix(black,blue,circle(uv,tan(iTime)+.4,.1,.12));
    val = mix(val,blue,circle(uv,tan(iTime)+1.2,.1,.12));
    val= mix(val,red,quad(uv,.1,.6,tan(iTime)+.8,.2));
