vec3 brown = vec3(0.3,0.2,0.2);
vec3 black = vec3(0.0,0.0,0.0);
vec3 red = vec3(0.7,0.0,0.0);
vec3 blue = vec3(0.,1.,1.);
vec3 yellow = vec3(1.0,1.0,0.0);
vec3 green = vec3(0.1,0.9,0.0);
vec3 white = vec3(1.0,1.0,1.0);

float wheels(vec2 uv, vec2 pos, float r)
{       
    float dist = distance(pos, uv);
    float value = step(r, dist);

    return value;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
        uv.x *= iResolution.x/iResolution.y;

    //First Car movement
    float offset = sin(iTime * 1.0) * 2.0 + 0.5;
    float offset2 = sin(iTime * 1.0) * 2.0 + 0.5;

    //2nd Car
    float offset3 = sin(-iTime * 1.0) * 2.0 + 0.5;
    float offset4 = sin(-iTime * 1.0) * 2.0 + 0.5;

    //First Car
    float height = step(0.1,uv.y) - step(0.3,uv.y);
    float width = step(offset + 0.15,uv.x) -step(offset + 0.55,uv.x);

    vec3 car = mix(black, red, height * width);

    height = step(0.19,uv.y) - step(0.3,uv.y);
    width = step(offset + 0.15,uv.x) -step(offset + 0.29,uv.x);

    car = mix(car, blue, height * width);

    float mWheels = wheels(uv, vec2(offset2 + 0.25,0.1), 0.05);
    vec3 wheelCol = mix(yellow, car, mWheels);

    float mWheels2 = wheels(uv, vec2(offset2 + 0.46,0.1), 0.05);
    wheelCol = mix(yellow, wheelCol, mWheels2);

    //2nd Car
    height = step(0.5,uv.y) - step(0.7,uv.y);
    width = step(offset3 + 0.15,uv.x) -step(offset3 + 0.55,uv.x);

    vec3 car2 = mix(black, blue, height * width);

    height = step(0.59,uv.y) - step(0.7,uv.y);
    width = step(offset3 + 0.4,uv.x) -step(offset3 + 0.55,uv.x);

    car2 = mix(car2, red, height * width);

    wheelCol = mix(car2, wheelCol, wheelCol);

    float mWheels3 = wheels(uv, vec2(offset4 + 0.23,0.5), 0.05);
    wheelCol = mix(yellow, wheelCol, mWheels3);

    float mWheels4 = wheels(uv, vec2(offset4 + 0.47,0.5), 0.05);
    wheelCol = mix(yellow, wheelCol, mWheels4);

    // Output to screen
    fragColor = vec4(wheelCol,1.0);
}
