float circle(vec2 uv,float distanceAa, float distanceAb,float ditanceBa,float distanceBb,float size)
{

    float val = distance(uv, vec2(distanceAa, distanceAb)) * distance(uv, vec2(ditanceBa, distanceBb));

	val = step(val, size);

    return val;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 orange = vec3((sin(iTime)+1.)/2.,(sin(iTime)+1.)/4.,0.);
    vec3 red= vec3(1.,.0,.0);
    vec3 blue= vec3(.0,.0,1.0);
    vec3 green = vec3(.0,1.,0);
    float ratio = iResolution.x/iResolution.y;
    uv.x *= ratio;


 	vec2 shapePos = vec2(.5*ratio,.5);
    float dist = distance(shapePos,uv)*2.;
    vec3 yellow= vec3(.9,.9,.3);
    uv -=shapePos;
    vec3 black = vec3(0.,0.,0.);
    float angle= atan(uv.y,uv.x);
    float radius = cos(2.*angle+iTime);
    radius = circle(uv,2.,2.,.3,.1,.4);
    float cir = circle(uv,2.2,2.2,sin(iTime)+.3,.2,.4);
    float value = 1.-step(radius,dist);
    float value2 = 1.-step(cir,dist);

    vec3 val = mix(black,blue, value);
    val = mix (val, green,circle(uv,2.,2.,.3,.2,.06));
    val = mix (val, green,circle(uv,2.,2.,.31,.24,.02));
    val = mix (val, green,circle(uv,2.,2.,.31,.15,.04));
    val = mix (val, green,circle(uv,2.,2.,.31,.09,.09));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.3,sin(iTime* 2.0)+.4,(sin(iTime) * sin(iTime) + 1.0)*.01));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.2,sin(iTime* 1.0)+.45,clamp(sin(iTime * 5.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.15,sin(iTime* 2.0)+.05,clamp(sin(iTime * 4.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.17,sin(iTime* 3.0)+.97,clamp(sin(iTime * 8.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.17,sin(iTime* 1.0)+.01,clamp(sin(iTime * 2.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,2.,2.,sin(iTime)+.27,sin(iTime* 3.0)+.01,clamp(sin(iTime * 2.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.02)));
    val = mix (val, yellow,circle(uv,2.,2.,iTime,.01,clamp(sin(iTime * 3.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,.1,2.,.27,.05, clamp(sin(iTime * 5.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));
    val = mix (val, yellow,circle(uv,.1,2.,sin(iTime)+.27,sin(iTime)+.05, clamp(sin(iTime * 5.0), 0.0, (sin(iTime) * sin(iTime) + 1.0)*.009)));


    // Output to screen
    fragColor = vec4(val,uv);
}
