#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
      vec4
        n = A(U+vec2(0,1)),
        e = A(U+vec2(1,0)),
        s = A(U-vec2(0,1)),
        w = A(U-vec2(1,0));
    vec2 g = vec2(e.z-w.z,n.z-s.z)+vec2(e.x-w.x,n.x-s.x);
    vec4 a = A(U);
    Q = 5.*a.x*a.y*vec4(0.2,.7,.9,1);
    Q += 5.*a.z*a.w*vec4(.7,.1,.4,1);
    Q += g.x+g.y;
}

#define R iResolution.xy
vec4 A (vec2 U) {return texture(iChannel0,U/R);}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    float a = iTime+3.*length(U), c = cos(a), s = sin(a);
    mat2 m = mat2(c,-s,s,c);
    #define a 2.
    #define o a*.7071
    vec4 n = A(U+vec2(0,a)*m)+A(U+vec2(a,0)*m)+A(U+vec2(0,-a)*m)+A(U+vec2(-a,0)*m)+A(U+vec2(-o,o)*m)+A(U+vec2(o,-o)*m)+A(U+vec2(-o,-o)*m)+A(U+vec2(o,o)*m);
    n *= .125;
    vec4 dx = n-Q;
    Q += dx*vec4(1,.2,1,.3);

    float x = .35*Q.x*Q.y*(1.-Q.y);
    float y = .4*Q.z*Q.w*(1.-Q.w);
    float z = 3.*Q.y*Q.w*(1.-n.y);
    float w = 3.*Q.w*Q.y*(1.-n.w);
    Q.yxwz = vec4(
    Q.y+x-0.03+z-w-Q.w,
    Q.x+0.1*Q.x*(1.-Q.x)-x,

    Q.w+y-0.03+w-z-Q.y,
    Q.z+0.1*Q.z*(1.-Q.z)-y);

    Q = max(Q,0.);

    if (iMouse.z> 0. && length(U-iMouse.xy) < 40.) Q*=0.;

    if (iFrame < 1) {

        Q = vec4(1,0,1,0);
        if (length(U-0.6*R)<5.) Q.w += 1.;
        if (length(U-0.3*R)<5.) Q.y += 1.;
    }
}

