vec3 red = vec3(1.,0.,0.);
vec3 blue = vec3(0.,0.,1.);
vec3 green = vec3(0,1.,0.);
vec3 yellow = vec3(1.,1.,0.);
vec3 black = vec3(0.,0.,0.);
vec3 gray = vec3(.5,.5,.3);



float gear(vec2 uv,float posX,float posY,float handSize,float midSize,float handNum)
{
    float ratio = iResolution.x / iResolution.y;

    uv.x *= ratio;

    vec2 shapePos = vec2(posX * ratio, posY);

    float dist = distance(shapePos, uv) * 2.0;

    uv -= shapePos;

    float speed = 0.5;

    float angle = (atan(uv.y, uv.x) + iTime * speed);

    float radius = 0.0;


    radius = smoothstep(-0.5, 1.0, cos(angle * handNum)) * handSize + midSize;


    float value = 1.0 - step(radius, dist);

    return value;

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 col = vec3(clamp(sin(iTime), 0.0, 1.0) - 1.0, 0.0, clamp(sin(iTime), 1.0, 1.0));

    vec3 value = mix(sin(iTime)*col,yellow,gear(uv,.08,.01,5.,.8,6.));



    // Output to screen
    fragColor = vec4(value,uv);    
}
