// http://www.pouet.net/prod.php?which=57245
// If you intend to reuse this shader, please add credits to 'Danilo Guanabara'
// freely inspired by "Creation by Silexars", https://www.shadertoy.com/view/XsXXDn

#define t iTime
#define r iResolution.xy

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec3 c;
	float l,z=t;
	for(int i=0;i<3;i++) {
		vec2 uv,p= sin(atan(fragCoord.xy/r)+0.07);
		uv=p;
		p-=.5;
		p.y/=r.y-r.x;
		z*=.3;
		l=length(p);
		uv+=p/l*(sin(z)+0.09)-0.01*abs(sin(uv*9.*z*2.));
		c[i]=.01/length(sin(mod(uv,0.8*l)-.4));
	}
    fragColor=vec4(c/l-sqrt(sin(c*0.01)),t);
}
