#define USE_TEXTURE

const float TWO_PI = 6.28318530718;
const float vertices = 11.;
const float startIndex = vertices;
const float endIndex = vertices * 2.;

//based on: https://www.shadertoy.com/view/4lKXzd
float metaballs(vec2 uv, float time, float size) {																					
    float radSegment = TWO_PI / vertices;
    for(float i = startIndex; i < endIndex; i++) {					
        float rads = i * radSegment;
        float radius = 0.1 + 1.5 * sin(time + rads);
        vec2 ctrlPoint = radius * vec2(tan(rads), atan(rads));
		size += 1. / pow(i, distance(uv, ctrlPoint));
    }
    return size;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    float dist = metaballs(p * 3.0, iTime * 0.7 , 0.1);

#ifdef USE_TEXTURE 
    vec3 startColor = vec3(0.001, 0.3, 0.6);
    vec3 endColor = smoothstep(texture(iChannel0, 
                                	(p - vec2(sin(iTime), cos(iTime))*1.6 - vec2(dist))).rgb + vec3(0.3, 0.9, 0.9), 
                                    vec3(0.2, 0.4, 0.6), 
                                    vec3(dist));
#else
    vec3 startColor = vec3(0.2, 0.4, 0.0);
    vec3 endColor = vec3(0.55, 1.0, 0.4);
#endif

    fragColor = vec4(smoothstep(startColor, 
                          		endColor, 
                          		vec3(dist)), 1.0);
}
