vec2 randompoint( in float anim, in float n )
{
    vec2 ab = vec2(sin(anim*0.01+n*n)+sin(anim*0.039)+cos(anim*0.0071),
                   cos(anim*0.011+n/17.0)+sin(anim*0.036+n*1.3)+cos(anim*0.0078));
    return vec2(1.25*sin(n*1.7+ab.x), cos(n+ab.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy - 0.5*iResolution.xy;
    uv = 2.0*uv/iResolution.y;

    float r =  length(uv - randompoint(iTime,  0.0));
    r = min(r, length(uv - randompoint(iTime,  1.0)));
    r = min(r, length(uv - randompoint(iTime,  2.0)));
    r = min(r, length(uv - randompoint(iTime,  3.0)));
    r = min(r, length(uv - randompoint(iTime,  4.0)));
    r = min(r, length(uv - randompoint(iTime,  5.0)));
    r = min(r, length(uv - randompoint(iTime,  6.0)));
    r = min(r, length(uv - randompoint(iTime,  7.0)));
    r = min(r, length(uv - randompoint(iTime,  8.0)));
    r = min(r, length(uv - randompoint(iTime,  9.0)));
    r = min(r, length(uv - randompoint(iTime, 10.0)));

    float width = 6.0 / 200.0;
    r = mod(r,width)/width;
    r = 2.0*abs(r-0.5);
    r = clamp(r, 0.25, 0.75);
    r = ((r - 0.5) / 0.25) + 0.5;
    float col = clamp( r, 0.0, 1.0);

    // Output to screen
    fragColor = vec4(col,col,col,1.0);
}
