// Built by yayromans just for educational purposes. Use as you please.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float grid = 50.0;
    vec4 white = vec4(1.0);
    vec4 black = vec4(0.0);

    // Pointer position
	vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y)
        - vec2(.4*cos(iTime), .4*sin(iTime)) 
        - vec2(.50, .50);

    // Plotter arms
    if ((uv.x > -0.01 && uv.x < 0.01) || (uv.y > -0.01 && uv.y < 0.01)) {
        fragColor = black;
        return;
    }

    // Oval
    if (uv.x*uv.x + uv.y*uv.y < 0.004 && uv.x*uv.x + uv.y*uv.y > 0.002) {
        fragColor = black;
        return;
    }

    // Grid
    for (float i = 0.0; i <= max(iResolution.x, iResolution.y); i+=grid) {
        if ((fragCoord.y >= i - 0.5 && fragCoord.y <= i + 0.5) || (fragCoord.x >= i - 0.5 && fragCoord.x <= i + 0.5)) {
            fragColor = black;
            return;
        }
    }

    // Background
    fragColor = white;
}
