void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 bg = uv;
	float t = iTime;

    float carre = 40.;

    bg.x *= iResolution.x/iResolution.y;

    uv.x -= cos(t*2.)*.3+.5;
    uv.y -= abs(cos(t*3.))*.4+.25;

    uv.x *= iResolution.x/iResolution.y;

    vec3 rgb;

    float c = length(uv);
    if(c<.25){

        float nimp = 4.;
        // following line is stolen from https://www.shadertoy.com/view/XstXzs
        float nah = asin(uv.x*nimp / cos(asin(uv.y*nimp)))*.25;

        vec2 plop;
        plop.x= cos(carre*nah+t*carre*.5)+.5;
        plop.y= cos(uv.y*carre)+.5;


        if(plop.x < 0.5 ^^ plop.y < 0.5 ){
            rgb=vec3(1,0,0);
        }
        else rgb=vec3(1,1,1);
    }
    else{
        vec2 plop = cos(bg*carre*1.5);
        if (plop.x > 0.99 || plop.y > 0.99) rgb=vec3(.5,0.,.5);
        else rgb=vec3(.7,.7,.7);
    }


    // Output to screen
    fragColor = vec4(rgb,1.0);
}
