// Texturing
// Eric Galin

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : point
// c : center of skeleton
// r : radius
V Sphere(vec3 p, vec3 c,float r,int index)
{
  return V(length(p-c)-r,index);
}

// Sphere 
// p : point
// n : Normal of plane
// o : Point on plane
V Plane(vec3 p, vec3 n, vec3 o,int index)
{
    return V(dot((p-o),n),index);
}

//Plan2D
//p : point
//n : normal of plan
//o : point of plan
//i : field function of right sub-tree
V Plane3D(vec3 p, vec3 n, vec3 o, int i)
{
    return V(Plane(p,n,o,i).v+Noise(p),i);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

//Turbulence
//p : point 
float Turbulence(vec3 p)
{
    float d=sqrt(p.x*p.x+p.y*p.y);
	float t=Noise(p);
    for (float i=0.5; i<0.05; i/=2.0)
    {
     	t+=i+Noise(p/i);   
    }
    t/=2.0;
 	return t; 
}


// Damier
// p : point
vec3 Damier (in vec3 p)
{
   if (sin(p.x) >= 0.0 == sin(p.y) >= 0.0 == sin(p.z) >= 0.0)
   {
       return vec3(0.0,0.0,0.0);
   }

   if (sin(p.x) <= 0.0 != sin(p.y) <= 0.0 != sin(p.z) <= 0.0)
   {
       return vec3(1.0,1.0,1.0);
   } 
}

// Damier
// p : point
// c1 : color
// c2 : color
vec3 Bois(vec3 p,vec3 c1,vec3 c2)
{
 	float d=sqrt(p.x*p.x+p.y*p.y)+Turbulence(p);
    float u=d/2.0;
    float s=sin(u*2.0*3.14);
    s=(s+1.0)/2.0;
    return mix(c1,c2,s);
}

//Translation
//p : point
//t : point
vec3 Translation(vec3 p, vec3 t)
{
 	vec3 v=p+t;
    return v;
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V v = Union(
      Sphere(Translation(p,vec3(0.0,0.0,-6.0*sin(iTime))),
             vec3( 0.0, 0.0, -1.0),sin(3.14*iTime/2.0)+5.0,1),
      Plane3D(p,vec3(0.0,0.0,1.0),vec3(0.0,0.0,-5.0+sin(iTime)),2));
  return v;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Lighting ----------------------------------------------------------------------------------------------

// Ambient occlusion
// p : Point
// n : Normal
// a : Number of smaples
float AmbientOcclusion(vec3 p,vec3 n, int a)
{
    if (a==0) { return 1.0; }

	float ao=0.0; 

    for (int i=0;i<a;i++)
    {
 		vec3 d = Cosine(581.123*float(i) , n);

        int s;
        bool h;
        float t=SphereTrace(p,d,10.0,h,s);
        if (!h) {ao+=1.0;}
        else if (t>5.0)
        {
             ao+=1.0;   
        }
    }

    ao/=float(a);
	return ao;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.65,0.75,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


// Shading and lighting ---------------------------------------------------------------------------


// Compute color 
// p : Point
// n : Normal
vec3 Color(in vec3 p,in vec3 n)
{
    V vp=object(p);
      switch (vp.i)
    {
        case 1: 
			return Damier (p);
        	break;
        default:
        	return Bois(p,vec3(0.6,0.2,0.0),vec3(1.0,0.8,0.4));
    		break;

    }
}

// Shade
// p : Point
// n : Normal at point
vec3 Shade(vec3 p, vec3 n)
{
    vec3 ambient=0.5*Color(p,n); 
    vec3 diffuse=0.35*Light(p,n)*Color(p,n);
   vec3 c = ambient+diffuse; 
     c+=0.15*AmbientOcclusion(p+0.1*n,n,0)*vec3(1.0,1.0,1.0);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

// Texturing and noise ---------------------------------------------------------

// Hashing function
// Returns a random number in [-1,1]
float Hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Lighting -----------------------------------------------------------------------

// Cosine direction 
// seed : Random seed
//    n : Normal
vec3 Cosine( in float seed, in vec3 n)
{
    float u = Hash( 78.233 + seed);
    float v = Hash( 10.873 + seed);

    // Method by fizzer: http://www.amietia.com/lambertnotangent.html
    float a = 6.2831853 * v;
    u = 2.0 * u - 1.0;
    return normalize( n + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u) );
}

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3(40.0,0.0,9.0);
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
