void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float ts = sin(iTime * 0.2);
    float tc = cos(iTime* 0.2);

    mat2 rotmat = mat2(tc, ts, -ts, tc);    
    uv *= rotmat;   

    float ata = atan(uv.x, uv.y) * 20.;   
    vec2 polar = vec2(atan(uv.x, uv.y), length(uv));
    float polarLenght = length(polar);

    float c =  1.0 - sin(ata) * 0.5 + 0.5;  
    if(polarLenght > (sin(iTime * 0.3) * 0.5 + 0.5) * 3.3)
    	c *= 0.0;

    vec3 col = texture(iChannel0, uv * c + iTime * 0.2).rgb * sqrt(c * 0.6) * 1.5;

    vec3 res = mix(1. - texture(iChannel1, polar).rgb, col, c); 

    fragColor = vec4(res, 1.0);
}
