//https://thebookofshaders.com/07/


float gear (vec2 uv, float r, float a)
{
     float rr = length(uv)*2./r;
	 a = atan(uv.y,uv.x)+a/r;
	float f = smoothstep(-.5,1., cos(a*r*10.))*0.2/r+0.5;
    return 1.-smoothstep(f,f+0.02,rr)-step(length(uv),.02);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

	vec2 pos = vec2(0.5)-uv;

    pos.x *=iResolution.x/iResolution.y;
    pos.y -= .2;

    vec3 color = vec3(0.0);

    //float r = length(pos)*2.0;
    //float a = atan(pos.y,pos.x);
    //a+=iTime;

     //f = cos(a*3.);
     //f = abs(cos(a*3.));
     //f = abs(cos(a*2.5))*.5+.3;
     //f = abs(cos(a*12.)*sin(a*3.))*.8+.1; 
     //f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;

	float f = gear(pos,.8,iTime)
        	+ gear(pos+vec2(-.7,.1),1.5,-iTime-.3)
        	+ gear(pos+vec2(.45,.45),1.2,-iTime);


    color = vec3( f );

    // Output to screen
    fragColor = vec4(color,1.0);
}
