#define rot(a)    mat2( cos(a+vec4(0,11,33,0)) )                           // rotation                  

void mainImage(out vec4 O, vec2 U) {    
    float d;
    vec3  R = iResolution, 
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(8,4,0)/1e2*cos(iTime+vec3(0,11,0)),
          D = normalize(vec3( U+U, -3.5*R.y ) - R ),                       // ray direction
          p = 2./R, C; 
    O-=O;
    p.xy *= rot( .5+6.*M.y), D.xy *= rot( .5+6.*M.y);                      // camera move 
    p.xz *= rot( 2.-6.*M.x), D.xz *= rot( 2.-6.*M.x);
    p.xy += cos( iTime + vec2(0,11)) - iTime;

    for ( float i=0.; i < 50.; i++,  p += D ) {                            // volume ray-casting
        vec4 T = texture( iChannel0, p/32. );
        C = fract(p) - T.rgb ;                                             // distance to sphere in voxel
        d = -dot(C,D);       // distance ray-sphere: d/dl ( || P+l.D - C ||Â² )  = 2l + 2 (CP.D)
        if (i>0. || d > 0.)
            C += d*D,        // point on the ray that is closest to sphere
        //  O += 3e-4 / dot(C,C)                                           // addition variant
            O += (1.-O)* clamp(1.- 1e3* dot(C,C), 0.,1. )                  // blend
                       * exp2(-.05*i);                                     // fog
                    // * ( .6 + .6 * cos( 6.3*T.w  + vec4(0,23,21,0)  ) ); // color version
    }
}
