#define MAX 20.

vec2 N21(float a){
    return(vec2(
    	sin(23.45*sin(23.1*a+423.512)),
        sin(7.345*sin(21.7*a+67.967))
    ));    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    float t = iTime / 10000.;

    float red = 0.; 
    float green = 0.;
    vec2 p = vec2(0.3,0.1);

    for(float i = 0.; i <= MAX; i += 1.){
    	p = N21(i+t);
        red += .1/(length(uv-p)*length(uv-p)*MAX);
    }
    for(float i = 0.; i <= MAX; i += 1.){
    	p = N21(i+143.+t);
        green += .1/(length(uv-p)*length(uv-p)*MAX);
    }

    if(red>green) col = vec3(255., 168., 175.)/256.;
    else col = vec3(0., 166., 147.)/256.;

    //col.rg = vec2(red,green);

    fragColor = vec4(col,1.0);
}
