vec2 mouse;

vec2 translate(vec2 uv) {
	uv -= floor(mouse.xy);
    return uv;
}

float random (vec2 uv) {
    return fract(sin(dot(uv, vec2(135., 263.))) * 103.214532);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    mouse = iMouse.xy/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
    float aspect = iResolution.x / iResolution.y;
	uv *= 9. * vec2(aspect, 1.);
    mouse *= 9. * vec2(aspect, 1.);

    uv = translate(uv);

    vec3 color = vec3(random(floor(uv))) + vec3(random(floor(uv + vec2(.2))), .3, .1);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
