#define TRANSITION_TYPE 2

const float timeOffset = 0.;
const float transitionTime = 0.4;
const float cycleTime = 3.;
const float mblurTime = 0.02;
const int mblurSteps = 30;
const float rTimeShift = 0.;
const float gTimeShift = 0.002;
const float bTimeShift = 0.004;
const float timeFactor = 1.;
// Try vec2(0., 1.), vec2(0., -1.), vec2(1., 0.) or vec2(-1., 0.)
const vec2 direction = vec2(0., 1.);

float getYPos(float time)
{
   float time2 = mod(time, cycleTime);
   #if TRANSITION_TYPE==1
   float yPos = smoothstep(0., transitionTime, time2);
   #elif TRANSITION_TYPE==2
   float yPos = 1. - exp(-2.5*time2/transitionTime)*cos(-3.2*time2/transitionTime);
   #endif

   return yPos;
}

vec3 mapImages(vec2 uv, float time)
{
   int imgNr = int(mod(time/cycleTime, 4.));    

   float yPos = getYPos(time);
   uv+= direction*yPos;
   vec3 col;

   bool comp = uv.x<0. || uv.x>1. || uv.y<0. || uv.y>1.;

   if (imgNr==0)
      col = comp?texture(iChannel1, fract(uv - direction)).rgb:texture(iChannel0, fract(uv)).rgb;
   if (imgNr==1)
      col = comp?texture(iChannel2, fract(uv - direction)).rgb:texture(iChannel1, fract(uv)).rgb;
   if (imgNr==2)
      col = comp?texture(iChannel3, fract(uv - direction)).rgb:texture(iChannel2, fract(uv)).rgb;
   if (imgNr==3)
      col = comp?texture(iChannel0, fract(uv - direction)).rgb:texture(iChannel3, fract(uv)).rgb;        

   return col;
}

vec3 mapImagesBlur(vec2 uv, float time)
{
   vec3 col = vec3(0.);
   for (int n = 0; n < mblurSteps; n++)
   {
      col+= mapImages(uv, max(time - mblurTime*float(n)/float(mblurSteps), 0.));
   }

   return col/float(mblurSteps);
}


vec3 mapImagesCD(vec2 uv, float time)
{
    vec3 col;
    col.r = mapImagesBlur(uv, time - rTimeShift).r;
    col.g = mapImagesBlur(uv, time - gTimeShift).g;
    col.b = mapImagesBlur(uv, time - bTimeShift).b;

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time2 = timeOffset + iTime*timeFactor - cycleTime + transitionTime;
    vec3 col = mapImagesCD(uv, time2);

    fragColor = vec4(col,1.0);
}
