float disk(vec2 pos, vec2 center, float r) {
    return 1.0-smoothstep(r, r+0.14, distance(pos, center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 p = fragCoord / iResolution.xy;
    vec2 pc = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.0);
    pc *= 9.21838192;

    float v = sin(pc.x*sin(t*0.2)+t*3.12)+sin(pc.y-sin(2.102*t))+sin(sin(length(pc))+t);
    for (int i=0; i<20; i++) {
        for (int j=0; j<20; j++) {
          	float d = disk(pc, vec2(v+10.1233*sin(float(i)/1.23+t*0.981),
                                    v+8.912381*sin(float(j)/0.991+t*0.914)), 0.0);
        	vec2 res = vec2(pow(d, 1.0), pow(d, 1.0));
        	col += vec3(res.x, res.y, length(p*d));
        }
    }

    //col.x += disk(pc, vec2(0.0), 0.0);
    fragColor = vec4(col,1.0);
}
