vec2 customUv(vec2 LL, vec2 UR, vec2 fragCoord){
    return(fragCoord/iResolution.xy*(UR+abs(LL))-abs(LL));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv;
    if(iMouse.z > 0.) {
    	uv = customUv(vec2(0.), 1./(iMouse.xy/iResolution.xy), fragCoord);
    } else {
    	uv = customUv(vec2(0.), vec2((sin(iTime)+10.)/11., (cos(iTime)+10.)/11.), fragCoord);
    }
    //vec2 uv = customUv(vec2(0.), vec2(1.), fragCoord);
    vec4 col = texture(iChannel0, uv);
    fragColor = vec4(col);
}
