vec2 blobs[120];
float strength = 0.03;
float speed = 0.003;
float cellsize = 1.0;
float nucleasize = 0.15;
float cellwallWidth = 0.05;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float val = 0.0;
  	for (int i = 0; i < blobs.length(); i++)
  	{
    	blobs[i] = vec2(
            fract(iTime*-11.0*speed + pow(float(i),1.2)*0.323 + sin(iTime*sin(float(i))*2.617924)*0.02),
            fract(iTime*17.0*speed + pow(float(i),1.3)*0.456 + cos(iTime*sin(float(i))*1.618)*0.02)
        );
        val += strength / pow(distance(uv, blobs[i]),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(1.0,0.0)),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(-1.0,0.0)),2.0);

        val += strength / pow(distance(uv, blobs[i] + vec2(0.0,1.0)),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(1.0,1.0)),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(-1.0,1.0)),2.0);

        val += strength / pow(distance(uv, blobs[i] + vec2(0.0,-1.0)),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(1.0,-1.0)),2.0);
        val += strength / pow(distance(uv, blobs[i] + vec2(-1.0,-1.0)),2.0);


  	}
    val /= float(blobs.length());


    float cellwall = max( floor(val*cellsize) - 2.0*floor(val*(cellsize - cellwallWidth)), 0.0);
    float inside = min(floor(val*cellsize - cellwallWidth), 1.0);
    float nucleus = min(floor(val*nucleasize), 1.0);
    vec3 col = vec3(
        0.719 - 0.719*cellwall + (0.719 - 0.578)*nucleus - 0.04*inside, 
        0.883 - 0.883*nucleus - 0.04*inside, 
        0.945 - 0.945*cellwall + (0.945 - 0.824)*nucleus
	);
    // Output to screen
    fragColor = vec4(col,1.0);
}
