// Fork of "square to hexagonal tiles" by Znah. 
// https://shadertoy.com/view/3d3BD4

//---------------------------------------------------------
// HexagonalSphericalFacets.glsl  2020-11-16
//---------------------------------------------------------

float facetHexPattern(vec2 u)
{
  vec2 s = vec2(1., 1.732);
  vec2 a = s-2.*mod(u,s);
  vec2 b = s-2.*mod(u+s*vec2(0.5,0.5),s);
  return(0.7+0.2*sin(iTime*1.1)-0.5*min(dot(a,a),dot(b,b)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 u = 22.*fragCoord/iResolution.x;
  fragColor = vec4(vec3(facetHexPattern(u)), 1.0);
}
